mapper.select:
SELECT fileName as "fileName", fileYear as "fileYear", fileULStatus as "fileULStatus"
FROM ems.adjustmentRequestFiles
WHERE fileULStatus = #{status}

mapper.update:
UPDATE ems.adjustmentRequestFiles SET
    fileULStatus = #{fileULStatus},
    filePath = #{filePath}
WHERE fileYear = #{fileYear} AND fileName = #{fileName}
